con:config.script:
import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext
log.info("om jai hanuma")
log.info(context.expand('${#TestCase#name}'))
log.info(context.expand('${#TestCase#id}'))

'''
// To access any custom property value from any testcase level 
testRunner.testCase.testSuite.testCases['GetEmp'].getPropertyValue('dept')
testRunner.testCase.testSuite.testCases['AddEmp'].getPropertyValue('age')

// To access any custom property value from any teststeps level
log.info(testRunner.testCase.testSuite.testCases['AddEmp'].testSteps['add'].getPropertyValue('AuthType'))

// To access any custom property value from project level
def projLevelProp = testRunner.testCase.testSuite.project.getPropertyValue('lkGlobal')
log.info(projLevelProp)

// To set any custom property value
testRunner.testCase.testSuite.testCases['AddEmp'].setPropertyValue('loc','BLR')
'''

// *********** Adding Employee *********** 
// grabbing xml request into a variable
def addXmlRequest = testRunner.testCase.testSuite.testCases['AddEmp'].testSteps['add'].getPropertyValue('Request')
// Setting Request XML
def xmlAdd = new XmlHolder(addXmlRequest)
def name = testRunner.testCase.testSuite.testCases['AddEmp'].getPropertyValue('name')
def id   = testRunner.testCase.testSuite.testCases['AddEmp'].getPropertyValue('id')
def dept = testRunner.testCase.testSuite.testCases['AddEmp'].getPropertyValue('dept')
def age  = testRunner.testCase.testSuite.testCases['AddEmp'].getPropertyValue('age')

xmlAdd.setNodeValue('//typ:addEmployee/typ:name',name)
xmlAdd.setNodeValue('//typ:addEmployee/typ:id',id)
xmlAdd.setNodeValue('//typ:addEmployee/typ:Department',dept)
xmlAdd.setNodeValue('//typ:addEmployee/typ:age',age)
def newXmlAdd = xmlAdd.getXml()

// setting the Request XML body
testRunner.testCase.testSuite.testCases['AddEmp'].testSteps['add'].setPropertyValue('Request',newXmlAdd)
//Calling the service
def addEmp = testRunner.testCase.testSuite.testCases['AddEmp'].testSteps['add']
def addEmpContext = new WsdlTestRunContext(addEmp)
addEmp.run(testRunner,addEmpContext)


// *********** Get Employee *********** 
// Getting Request 
def getXmlEmp = testRunner.testCase.testSuite.testCases['GetEmp'].testSteps['get'].getPropertyValue('Request')
def xmlGet = new XmlHolder(getXmlEmp)
xmlGet.setNodeValue('//typ:getEmployeeDetails/typ:employeeName',name)

def newXmlGet = xmlGet.getXml()

testRunner.testCase.testSuite.testCases['GetEmp'].testSteps['get'].setPropertyValue('Request',newXmlGet)
def getEmp = testRunner.testCase.testSuite.testCases['GetEmp'].testSteps['get']
def getEmpContext = new WsdlTestRunContext(getEmp)
getEmp.run(testRunner,getEmpContext)

// Validation 
def getResponse = testRunner.testCase.testSuite.testCases['GetEmp'].testSteps['get'].getPropertyValue('Response')
def getXmlResponse = new XmlHolder(getResponse)
def res= getXmlResponse.getNodeValue('//ns:getEmployeeDetailsResponse/ns:return/ns:name')
log.info(res)
log.info(name)
assert res==name


// *********** Delete Employee *********** 
def delXmlRequest = testRunner.testCase.testSuite.testCases['DelEmp'].testSteps['del'].getPropertyValue('Request')
def xmlDel = new XmlHolder(delXmlRequest)
xmlDel.setNodeValue('//typ:deleteEmployee/typ:employeeName',name)

def newXmlDel = xmlDel.getXml()
testRunner.testCase.testSuite.testCases['DelEmp'].testSteps['del'].setPropertyValue('Request',newXmlDel)
def delEmp = testRunner.testCase.testSuite.testCases['DelEmp'].testSteps['del']
def delEmpContext = new WsdlTestRunContext(delEmp)
delEmp.run(testRunner,delEmpContext)
